import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class HrisAccountLookupController extends LookupController
{
    @Service("TagabukidLookupService")
    def svc

    Column[] getColumns() {
        return [
            new Column(name:"entity.name", caption:"Name", width:250),
            new Column(name:"entity.acronymabbreviation", caption:"Code", width:250),
        ]
    }

    List fetchList( Map params ) { 
        return svc.lookupTranche(params)
    }
}
